import FormData from "form-data";
import axios from "axios";
import { pinataUrl } from "@constant";

export const uploadFileToIpfs = async (file) => {
    const data1 = new FormData();
    data1.append("file", file);
    data1.append("pinataOptions", JSON.stringify({ cidVersion: 1 }));
    data1.append(
        "pinataMetadata",
        JSON.stringify({
            name: file.name,
            keyvalues: { company: "Humans" },
        })
    );

    const config = {
        method: "post",
        // url: "https://api.pinata.cloud/pinning/pinJSONToIPFS",  // for uploading JSON
        url: "https://api.pinata.cloud/pinning/pinFileToIPFS", // for uploading image
        headers: {
            "Content-Type": "application/json",
            Authorization:
                "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..wEwg8WB0XqnT96kEmD3sPDvRmZxlGiUN1DgLSNJXdec",
        },
        data: data1,
    };
    try {
        const result = await axios(config);
        return result?.data?.IpfsHash;
    } catch (e) {
        throw new Error(e);
    }
};

export const uploadJSONToIpfs = async (name, data) => {
    const uploadData = JSON.stringify({
        pinataOptions: {
            cidVersion: 1,
        },
        pinataMetadata: {
            name,
            // keyvalues: {
            //     customKey: "customValue",
            //     customKey2: "customValue2",
            // },
        },
        pinataContent: data,
    });
    const config = {
        method: "post",
        url: "https://api.pinata.cloud/pinning/pinJSONToIPFS", // for uploading JSON
        headers: {
            "Content-Type": "application/json",
            Authorization:
                "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..wEwg8WB0XqnT96kEmD3sPDvRmZxlGiUN1DgLSNJXdec",
        },
        data: uploadData,
    };
    try {
        const result = await axios(config);
        return result?.data?.IpfsHash;
    } catch (e) {
        throw new Error(e);
    }
};

export const getImageFromHash = (hash) => `${pinataUrl}/${hash}`;
